	public void testPushApi() {

		Map<String, Object> map = new HashMap<>();
		map.put("Title", "附件测试");
		map.put("Content", Base64Util.encode("2018我来了"));
		map.put("FileName", "test");
		map.put("FileType", "txt");
		map.put("ChnlId", "812");
		map.put("Modified", "2017-02-11 12:50:20");
		File file = new File("C://Users//chenli//Desktop//aa.txt");
		byte[] bs = null;
		try {
			bs = FileUtils.readFileToByteArray(file);
		} catch (IOException e) {
			e.printStackTrace();
		}
		map.put("LogFiles", Base64Util.encodeBase64(bs, false));

		HttpHeaders headers = new HttpHeaders();
		// 请勿轻易改变此提交方式，大部分的情况下，提交方式都是表单提交
		headers.setContentType(MediaType.APPLICATION_FORM_URLENCODED);
		// 封装参数，千万不要替换为Map与HashMap，否则参数无法传递
		MultiValueMap<String, String> params = new LinkedMultiValueMap<String, String>();
		// 也支持中文
		String data = JSON.toJSONString(map);
		String appId = "10000";
		String appSecret = "8V1WRD2hBAMP9IKs";
		params.add("data", data);
		params.add("appId", appId);
		params.add("appSecret", appSecret);
		String digest = DigestUtils.md5Hex(appId + "&" + appSecret + "&" + Base64Util.encode(data));
		System.out.println("请求签名摘要： " + digest);
		params.add("digest", digest);
		HttpEntity<MultiValueMap<String, String>> requestEntity = new HttpEntity<MultiValueMap<String, String>>(params,
				headers);
		// http://27.22.125.133/data-push
		ResponseEntity<JsonResult> respResult = restTemplate.postForEntity(
				"http://27.22.125.133/data-push/api/v1.0/purchase/push", requestEntity, JsonResult.class);
		System.out.println(JSON.toJSONString(respResult.getBody()));
	}

